import { NgModule, ModuleWithProviders } from '@angular/core';
import { CommonModule } from '@angular/common';
import { CoreComponent } from './src/core.component';
import { CoreDirective } from './src/core.directive';
import { CorePipe } from './src/core.pipe';
import { CoreService } from './src/core.service';

export * from './src/core.component';
export * from './src/core.directive';
export * from './src/core.pipe';
export * from './src/core.service';

@NgModule({
  imports: [
    CommonModule
  ],
  declarations: [
    CoreComponent,
    CoreDirective,
    CorePipe
  ],
  exports: [
    CoreComponent,
    CoreDirective,
    CorePipe
  ]
})
export class CoreModule {
  static forRoot(): ModuleWithProviders {
    return {
      ngModule: CoreModule,
      providers: [CoreService]
    };
  }
}
