import { Injectable } from '@angular/core';

@Injectable()
export class CoreService {

  minhavariavel: string;
  novaVariavel: number;

  constructor() {
    this.minhavariavel = 'Sou uma Variavel';
  }

  retornaNovaVariavel(){
    return this.novaVariavel;
  }

  retornaVariavel(){
    return this.minhavariavel;
  }

}
